import React, { useEffect, useState } from "react";
import { ToDo } from "../../interfaces/toDoList";
import ToDoItem from "../ToDoItem";

const ToDoList: React.FC = () => {
  const [todos, setTodos] = useState<ToDo[]>(
    JSON.parse(localStorage.getItem("todos") || "[]")
  );

  const [task, setTask] = useState("");

  const handleAdd = (e: React.SyntheticEvent) => {
    e.preventDefault();
    if (task.trim() !== "") {
      const newTodo: ToDo = {
        id: todos.length + 1,
        task,
        done: false,
      };
      setTodos([...todos, newTodo]);
      setTask("");
    }
  };

  useEffect(() => {
    setTodos(JSON.parse(localStorage.getItem("todos") || "[]"));
  }, []);

  useEffect(() => {
    if (todos.length >= 0) {
      localStorage.setItem("todos", JSON.stringify(todos));
    }
  }, [todos]);

  const handleUpdate = (id: number, updatedTask: string) => {
    const updatedTodos = todos.map((todo) =>
      todo.id === id ? { ...todo, task: updatedTask } : todo
    );
    setTodos(updatedTodos);
  };

  const handleDone = (id: number) => {
    const updatedTodos = todos.map((todo) =>
      todo.id === id ? { ...todo, done: !todo.done } : todo
    );
    setTodos(updatedTodos);
  };

  const handleRemove = (id: number) => {
    const updatedTodos = todos.filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
  };

  return (
    <div class="todo-list">
      <form class="todo-list__form" onSubmit={handleAdd} action="">
        <input
          type="text"
          value={task}
          onChange={(e) => setTask(e.target.value)}
          placeholder="Add new task"
          class="todo-list__form__input"
          data-testid="task-input"
        />
        <button
          type="submit"
          class="todo-list__form__button"
          data-testid="add-button"
        >
          Add
        </button>
      </form>

      <ul class="todo-list__items">
        {todos.map((todo) => (
          <ToDoItem
            key={todo.id}
            id={todo.id}
            task={todo.task}
            done={todo.done}
            handleUpdate={handleUpdate}
            handleDone={handleDone}
            handleRemove={handleRemove}
            class="todo-list__item"
            data-testid={`todo-item-${todo.id}`}
          />
        ))}
      </ul>
    </div>
  );
};

export default ToDoList;
