import React, { useState } from "react";
import { ToDoItemI } from "../../interfaces/ToDoItem";

const ToDoItem: React.FC<ToDoItemI> = ({
  id,
  task,
  done,
  handleUpdate,
  handleDone,
  handleRemove,
}) => {
  const [editMode, setEditMode] = useState(false);
  const [updatedTask, setUpdatedTask] = useState<string>(task);

  const handleSave = () => {
    if (updatedTask.trim() !== "") {
      handleUpdate(id, updatedTask);
      setEditMode(false);
    }
  };

  return (
    <li data-testid={`todo-item-${id}`}>
      {editMode ? (
        <>
          <input
            type="text"
            value={updatedTask}
            onChange={(e) => setUpdatedTask(e.target.value)}
            data-testid={`todo-item-${id}-edit`}
          />
          <button onClick={handleSave} data-testid={`todo-item-${id}-save`}>
            Save
          </button>
        </>
      ) : (
        <>
          <span
            style={{
              textDecoration: done ? "line-through" : "none",
            }}
            data-testid={`todo-item-${id}-task`}
          >
            {task}
          </span>
          <button
            onClick={() => handleDone(id)}
            data-testid={`todo-item-${id}-done`}
          >
            Done
          </button>
          <button
            onClick={() => setEditMode(true)}
            data-testid={`todo-item-${id}-edit`}
          >
            Edit
          </button>
          <button
            onClick={() => handleRemove(id)}
            data-testid={`todo-item-${id}-remove`}
          >
            Remove
          </button>
        </>
      )}
    </li>
  );
};

export default ToDoItem;
